import React, { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="fixed w-full top-0 z-50 bg-black/50 backdrop-blur-sm">
      <div className="max-w-[1100px] mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex-shrink-0">
            <a href="/" className="text-2xl font-bold text-white">Beach<span className="text-amber-500">Resort</span></a>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:block">
            <div className="ml-10 flex items-center space-x-8">
              <a href="/" className="text-white hover:text-amber-500 transition-colors duration-300">Home</a>
              <a href="/rooms" className="text-white hover:text-amber-500 transition-colors duration-300">Rooms</a>
              <a href="/amenities" className="text-white hover:text-amber-500 transition-colors duration-300">Amenities</a>
              <a href="/contact" className="text-white hover:text-amber-500 transition-colors duration-300">Contact</a>
              <a href="/book" className="bg-amber-500 hover:bg-amber-600 text-white px-4 py-2 rounded-full transition-colors duration-300">
                Book Now
              </a>
            </div>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-white hover:text-amber-500 focus:outline-none"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                {isOpen ? (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                ) : (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                )}
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1">
              <a href="/" className="block text-white hover:text-amber-500 transition-colors duration-300">Home</a>
              <a href="/rooms" className="block text-white hover:text-amber-500 transition-colors duration-300">Rooms</a>
              <a href="/amenities" className="block text-white hover:text-amber-500 transition-colors duration-300">Amenities</a>
              <a href="/contact" className="block text-white hover:text-amber-500 transition-colors duration-300">Contact</a>
              <a href="/book" className="block w-full bg-amber-500 hover:bg-amber-600 text-white px-4 py-2 rounded-full transition-colors duration-300 mt-4 text-center">
                Book Now
              </a>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
